let wikiLink=`https://en.wikipedia.org/w/api.php?action=query&origin=*&format=json&list=search&srsearch=`;let searchTerm=document.getElementById('searchField')
let output=document.getElementById('output');let $outputButton=$('button');function capitalizeFirstLetter(string){return string.charAt(0).toUpperCase()+string.slice(1)}
$outputButton.on('click',function(e){let url=wikiLink+searchTerm.value;fetch(url).then(resp=>resp.json()).then(function(data){let html='';let activeLink='https://en.wikipedia.org/wiki/';data.query.search.map(function(item){html+=`<section class='output-card'>
                  <a href='${activeLink}${item.title}' target='_blank'>
                    <h4>${item.title}</h4>
                    <p>${capitalizeFirstLetter(item.snippet)}.</p>
                  </a>
                  </section>`;output.innerHTML=html})})});document.addEventListener('keydown',function(event){if(event.keyCode===13){let url=wikiLink+searchTerm.value;fetch(url).then(resp=>resp.json()).then(function(data){let html='';let activeLink='https://en.wikipedia.org/wiki/';data.query.search.map(function(item){html+=`<section class='output-card'>
                  <a href='${activeLink}${item.title}' target='_blank'>
                    <h4>${item.title}</h4>
                    <p>${capitalizeFirstLetter(item.snippet)}.</p>
                  </a>
                  </section>`;output.innerHTML=html})})}})
